/** @jsx jsx */
import { jsx } from '@emotion/core'

const styles = {
  form: {
    borderTop: '2px solid #373B44',
    padding: '.5rem',
    display: 'flex',
  },
  content: {
    flex: '1 1 auto',
    marginRight: '.5rem'
  },
  send: {
    backgroundColor: '#D6DDEC',
    padding: '.2rem .5rem',
    border: 'none',
    ':hover': {
      backgroundColor: '#2A4B99',
      cursor: 'pointer',
      color: '#fff',
    },
  }
}

export default ({
  addMessage
}) => {
  const onSubmit = (e) => {
    console.log(e.keyCode)
    e.preventDefault()
    const data = new FormData(e.target)
    addMessage({
      content: data.get('content'),
      author: 'david',
      creation: Date.now(),
    })
    e.target.elements.content.value = ''
  }

  const onKeyPress = (e) => {
    // if (e.key === 'Enter' && !e.shiftKey) onSubmit();
  }

  return (
    <form css={styles.form} onKeyPress={onKeyPress}  onSubmit={onSubmit}>
      <textarea type="Text" name="content" css={styles.content} />
      <input type="submit" value="Send" css={styles.send} />
    </form>
  )
}
