/** @jsx jsx */
import { jsx } from '@emotion/core'

const styles = {
  form: {
    borderTop: '2px solid #373B44',
    padding: '.5rem',
    display: 'flex',
  },
  content: {
    flex: '1 1 auto',
    marginRight: '.5rem'
  },
  send: {
    backgroundColor: '#D6DDEC',
    padding: '.2rem .5rem',
    border: 'none',
    ':hover': {
      backgroundColor: '#2A4B99',
      cursor: 'pointer',
      color: '#fff',
    },
  }
}

export default ({
  addMessage
}) => {
  let message = '';

  /*const onSubmit = (e) => {
    console.log(e)
    e.preventDefault()
    const data = new FormData(e.target)
    addMessage({
      content: data.get('content'),
      author: 'david',
      creation: Date.now(),
    })
    e.target.elements.content.value = ''
  }

  return (
    <form css={styles.form} onSubmit={onSubmit}>
      <input type="Text" name="content" css={styles.content} />
      <input type="submit" value="Send" css={styles.send} />
    </form>
  )*/

  const submit = () => {
    if (message === '') return
    addMessage({
      content: message,
      author: 'david',
      creation: Date.now(),
    })
    message = ''
    document.getElementById("messageTextArea").value = ``
  }

  const onChange = (e) => {
    message = e.target.value
  }

  const onKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) submit()
  }

  return (
    <form css={styles.form}>
      <textarea id="messageTextArea" onChange={onChange} onKeyPress={onKeyPress} css={styles.content}/>
      <button css={styles.send} onClick={submit}>Send</button>
    </form>
  )
}
